import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import "./TimelineEvent.css";

const TimelineEvent = ({ event }) => {
  const containerRef = useRef(null);
  const galleryImages = event.gallery || [];

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    const container = containerRef.current;

    let tl = gsap.timeline({
      scrollTrigger: {
        pin: true,
        scrub: 1,
        trigger: container,
        end: () => container.scrollWidth - document.documentElement.clientWidth,
      },
      defaults: { ease: "none", duration: 1 },
    });

    tl.to(".parallax", { x: 300 })
      .to(".panel_img", {
        x: () => -(container.scrollWidth - document.documentElement.clientWidth),
      }, 0);

  }, []);

  return (
    <div ref={containerRef} className="TimelineEvent-container">
      <div className="TimelineEvent-arrowBox">{event.year}</div>
      <div className="timeline-image">
          <img src={event.image} alt={event.description} />
        </div>
      <div className="timeline-event">
        {/* Original content here */}
        <div className="portfolio">
          {galleryImages.map((imgSrc, index) => (
            <div className="panel" key={index}>
              <div className="panel_item">
                <img className="panel_img parallax" src={imgSrc} alt={`Gallery Image ${index}`} />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TimelineEvent;
