import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import TimelineEvent from './components/TimelineEvent';
//import FallingStars from './components/FallingStars';  // Import the FallingStars component
import 'font-awesome/css/font-awesome.min.css';
import About from './components/About'
import {FULL_NAME, NON_ENGLISH_FULL_NAME , EPERIENCE_EVENTS} from './personalizationConstants';

function App() {
  const [bgColor, setBgColor] = useState('rgba(0, 0, 0, 1)');
  const [theme, setTheme] = useState(''); // this line for theme change
  const [timelineEvents, setTimelineEvents] = useState([]);
  const [activeTimeline, setActiveTimeline] = useState('experiences'); // 'projects', 'volunteer', or 'experiences'
  const aboutRef = useRef(null); // to scroll to about section
  const [sectionToScroll, setSectionToScroll] = useState(null);

  const scrollToSection = () => {
    if (sectionToScroll === 'about') {
      // Assuming you give your About section a ref named aboutRef
      aboutRef.current.scrollIntoView({ behavior: 'smooth' });
    } else if (sectionToScroll === 'timeline') {
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    }
  };


  useEffect(() => {
    if (sectionToScroll) {

      scrollToSection();
      setSectionToScroll(null);  // Resetting the state
    }
  }, [sectionToScroll]); // <- this should not cause an infinite loop, assuming `scrollToSection` doesn't change `sectionToScroll`.


  useEffect(() => {
    // Define your sets of timeline events
    const projectEvents = [
      {
        "type": "iOS Developer",
        "title": "Social iOS App",
        "date": "Aug 2023 - Present",
        "description": [
          "Incorporated Firebase for storage & login authentication.",
          "Integrated real-time data syncing between local storage and Cloud Firestore, ensuring a seamless user experience.",
          "Implemented the MVVM design pattern for a scalable and maintainable codebase."
        ],
        "skills": ["SwiftUI", "Cloud Firestore", "Firebase", "Data Synchronization", "Model-view-viewmodel (MVVM)", "Swift (Programming Language)"],
        "directory": "./experiences_images/social_ios_app/",
        "gallery_size": 0
      },
      {
        "type": "Full-stack Developer",
        "title": "Algorithm Visualizer",
        "date": "Mar 2022 - May 2022",
        "description": [
          "Visualized BST, Min/Max Heap operations.",
          "Backend: JS & Node.js | Frontend: HTML & CSS.",
          "Built an Undo feature using a stack of deep copied history.",
          "Implemented a BFS-based node positioning algorithm for binary tree illustration.",
          "Created a 'current action' box, showing the current action in the algorithm in real-time."
        ],
        "skills": ["JS", "Node.js", "HTML", "CSS"],
        "directory": "./experiences_images/algorithm_visualizer/",
        "gallery_size": 0
      }
        ,      
      {
        type: "Data Scientist",
        title: "Data Science Tutorial",
        date: 'Nov 2021 - Dec 2021',
        description: [
          "Created a step-by-step introduction to data science using Jupyter Notebook.",
          "Walked the reader through Data Collection, Processing, Visualization, and Machine Learning.",
          "Showcased the steps needed to tidy different types of tables into easy to manipulate data frames.",
          "Demonstrated skewed data handling techniques, like logarithmic representation and trimming outliers.",
          "Presented hypothesis testing and prediction with single-variable vs multivariable modules."
        ]
        ,        
        skills: ['Python', 'Pandas', 'Jupyter', 'Data Visualization', 'Machine Learning'],
        directory: './experiences_images/data_science_tutorial/',
        gallery_size: 0
      },
      {
        type: "Python Developer",
        title: "Maze Generator",
        date: 'Aug 2020',
        description: [
          "Created a visualized random maze generator with the Pygame library.",
          "Implemented the randomized depth-first search (DFS) method."
        ],
        skills: ['Python', 'Pygame', 'DFS'],
        directory: './experiences_images/maze_generator/',
        gallery_size: 0 
      }

    ];



    const volunteerEvents = [
      {
        type: "Mentoring at Bitcamp Hackathon",
        title: "Bitcamp Hackathon",
        date: 'Apr 2023',
        description: [
          "Helped participants with app design and ML implementation at Bitcamp 2023 Hackathon."
        ],
        skills: ["Mentoring", "Machine Learning", "App Design", "Connecting"],
        directory: './volunteering_images/bitcamp/',
        gallery_size: 2
      },
      {
        type: "Scorer & Checker at MATHCOUNTS Foundation",
        title: "MATHCOUNTS Competition",
        date: 'Feb 2023',
        description: [
          "Worked in a team of 8, scoring and checking 200+ answer sheets at MATHCOUNTS (the math version of the Spelling Bee)."
        ],
        skills: ["Teamwork", "Attention to Detail", "Mathematics", "Communication"],
        directory: './volunteering_images/mathcounts/',
        gallery_size: 2
      },
      {
        type: "Mentoring at Technica Hackathon",
        title: "Technica Hackathon",
        date: 'Oct 2022',
        description: ["Helped participants with web development and other programming-related issues at Technica Hackathon."],
        skills: ["Web Development", "Problem Solving", "Mentoring", "Active Listening"],
        directory: './volunteering_images/technica/',
        gallery_size: 0
      },
      {
        type: "Church Renovation in Austin",
        title: "The Project, University of Texas Austin",
        date: 'Jan 2020',
        description: ["Repainted the walls and restored the doors of a church in Austin."],
        skills: ["Painting", "Restoration", "Manual Labor", "Team Building"],
        directory: './volunteering_images/the_project/',
        gallery_size: 0
      },
      {
        type: "SAT Tutor at my highschool",
        title: "SAT",
        date: 'Sep 2018 - Dec 2018',
        description: ["Held bi-weekly sessions for SAT (Math and English) to help 20 younger students in my high school."],
        skills: ["Teaching", "SAT Preparation", "Mathematics", "English", "Leadership"],
        directory: './volunteering_images/highschool_tutor/',
        gallery_size: 0
      }
    ];

    const experienceEvents = EPERIENCE_EVENTS;





    // Set timelineEvents based on activeTimeline
    if (activeTimeline === 'projects') {
      setTimelineEvents(projectEvents);
    } else if (activeTimeline === 'volunteer') {
      setTimelineEvents(volunteerEvents);
    } else if (activeTimeline === 'experiences') {
      setTimelineEvents(experienceEvents);
    }
  }, [activeTimeline]);



  useEffect(() => {
    document.body.style.backgroundColor = bgColor;
    return () => {
      document.body.style.backgroundColor = null;
    };
  }, [bgColor]);




  //<FallingStars /> {/* Include the FallingStars component */}
  // put this below to make stars







  return (
    <div className={`App ${theme}`} style={{ backgroundColor: bgColor }}>


    <header className="App-header">
      <h1 className="title-english">FULL_NAME</h1>

      <div className="TabMenu">
        <button 
          className={activeTimeline === 'projects' ? 'active' : ''} 
          onClick={() => {
            setActiveTimeline('projects');
            setSectionToScroll('timeline');
          }}
        >
          Projects
        </button>
        <button 
          className={activeTimeline === 'volunteer' ? 'active' : ''} 
          onClick={() => {
            setActiveTimeline('volunteer');
            setSectionToScroll('timeline');
          }}
                >
          Volunteer
        </button>
        <button 
          className={activeTimeline === 'experiences' ? 'active' : ''} 
          onClick={() => {
            setActiveTimeline('experiences');
            setSectionToScroll('timeline');
          }}
        >
          Experiences
        </button>
        <button onClick={() => setSectionToScroll('about')}>
          About
        </button>
      </div>

      <h1 className="title-arabic">NON_ENGLISH_FULL_NAME</h1>


        <div className="social-icons">
          <a href="https://www.youtube.com/@tahaalnufaili8838" target="https://www.youtube.com/@tahaalnufaili8838" rel="noopener noreferrer">
            <i className="fa fa-youtube fa-2x"></i>
          </a>
          <a href="https://www.linkedin.com/in/taha-al-nufaili/" target="https://www.linkedin.com/in/taha-al-nufaili/" rel="noopener noreferrer">
            <i className="fa fa-linkedin fa-2x"></i>
          </a>
          <a href="https://github.com/ta0a2000t" target="https://github.com/ta0a2000t" rel="noopener noreferrer">
            <i className="fa fa-github fa-2x"></i>
          </a>
          <a href="https://www.youtube.com/watch?v=7eAdsUbXkaQ&ab_channel=MrMahir%D9%85%D8%A7%D9%87%D8%B1" target="resume.pdf" rel="noopener noreferrer">  {/* Update this URL to point to your actual resume */}
            <i className="fa fa-file-text fa-2x"></i>
          </a>
        </div>

    </header>




      <section className="Timeline">


        <h2>{activeTimeline === 'projects' ? 'My Projects üõ†' : activeTimeline === 'volunteer' ? 'My Volunteer Work ü§ù' : 'My Experiences üõ∂'}</h2>
        {timelineEvents.map((event, index) => (
          <TimelineEvent key={index} event={event} />
        ))}
      </section>

      <section id="about" ref={aboutRef}>
        <About/>
      </section>


    </div>


  );
}

export default App;
