import React, { useState, useEffect } from 'react';
import './App.css';
import Project from './components/Project';
import Experience from './components/Experience';
import TimelineEvent from './components/TimelineEvent';
import FallingStars from './components/FallingStars';  // Import the FallingStars component

function App() {
  const [bgColor, setBgColor] = useState('rgba(0, 0, 0, 0.5)');
  const [theme, setTheme] = useState(''); // Add this line for theme change
  const [timelineEvents, setTimelineEvents] = useState([]);

  // Simulate fetching data from an API
  useEffect(() => {
    setTimelineEvents([
      {type:"Internshi AADFEwfewefwe", title:"First Event", date: '2023, May', description: 'Event 1', image: 'image1.png', gallery: ['image1.png', 'image2.png'] },
      {type:"Volunteer", title:"Second Event", date: '2023, Jun', description: 'Event 2', image: 'image2.png' },
    ]);
  }, []);

  useEffect(() => {
    const updateBackground = (e) => {
      const x = e.clientX;
      const y = e.clientY;
      const z = (x + y) / 2;

      // Here we are reducing the range to 200-255, which limits the color change
      const red = Math.floor((x / window.innerWidth) * 55 + 200);
      const green = Math.floor((y / window.innerHeight) * 55 + 200);
      const blue = Math.floor((z / ((window.innerWidth + window.innerHeight) / 2)) * 55 + 200);

      setBgColor(`rgba(${red}, ${green}, ${blue}, 0.5)`);  // Alpha set to 0.5 for transparency
    };


    const toggleNightTheme = (e) => {
      if (e.clientX > window.innerWidth / 2) {
        setTheme('App-night');
      } else {
        setTheme('');
      }
    }

    //window.addEventListener('mousemove', updateBackground);
    //window.addEventListener('mousemove', toggleNightTheme);  // Add this line for theme change

    return () => {
      window.removeEventListener('mousemove', updateBackground);
      window.removeEventListener('mousemove', toggleNightTheme); // Add this line for theme change
    };
  }, []);

  const [headerColor, setHeaderColor] = useState("#282c34"); // State to keep track of the header color

  const handleMouseEnter = () => {
    setHeaderColor("#444"); // Change to a different color when the mouse enters
  };

  const handleMouseLeave = () => {
    setHeaderColor("#282c34"); // Reset to the original color when the mouse leaves
  };
  //<FallingStars /> {/* Include the FallingStars component */}
  // put this below to make stars

  return (
    <div className={`App ${theme}`} style={{ backgroundColor: bgColor }}>

      <header className="App-header" 
                style={{ backgroundColor: headerColor }} 
                onMouseEnter={handleMouseEnter} 
                onMouseLeave={handleMouseLeave}>
          <h1>My Portfolio</h1>
      </header>

      <section className="Timeline">
        <h2>My Journey</h2>
        {timelineEvents.map((event, index) => (
          <TimelineEvent key={index} event={event} />
        ))}
      </section>


    </div>
  );
}

export default App;
