 *  GitHub: https://github.com/ta0a2000t
 *  
 *  Notice: This file is part of a project created by Taha Al-Nufaili. 
 *  Redistribution of this file is permitted. However, modification 
 *  of this file is strictly prohibited without explicit permission 
 *  from the author.
 *
 **********************************************************************/

import React, { useEffect, useState } from 'react';
import './TimelineEvent.css';
import ImageGallery from './ImageGallery';

const TimelineEvent = ({ event }) => {


  const EventDescription = ({ description }) => {
    const descriptionList = description.map((line, index) => (
      <li key={index}>{line}</li>
    ));

    return (
      <div>
        <ul className="timeline-description">
          {descriptionList}
        </ul>
      </div>
    );
  };

  const SkillList = ({ skills }) => {
    return (
      <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
        {skills.map((skill, index) => (
          <div key={index} className="skill-item">
            {skill}
          </div>
        ))}
      </div>
    );
  };

  const ArrowIcon = ({ link }) => {
    const iconClass = link ? "fa-arrow-up-right-from-square" : "fa-arrow-up-right-from-square disabled-icon";

    return (
      <div>
        {link ? (
          <a href={link} target="_blank" rel="noopener noreferrer">
            <i className={`fa ${iconClass} fa-lg`}></i>
          </a>
        ) : (
          <i className={`fa ${iconClass} fa-lg`}></i>
        )}
      </div>
    );
  };




  return (
    <div className="TimelineEvent-container">
      <div className="TimelineEvent-arrowBox"></div>
      <div className="timeline-event">
        <span className="event-date">{event.date}</span>
        <div className="timeline-top"> 
          <div className="timeline-image">
              <img src={`${event.directory}/logo.png`} alt={event.title} loading="lazy" />
          </div>
          <div className="timeline-content">
            <h3>{event.title}</h3>
          </div>
          <ArrowIcon link={event.link} />


        </div>
        <h2 style={{ marginBottom: '0em' }}>{event.type}</h2>
        <EventDescription description={event.description} style={{ marginTop: '0em' }}/>
        <SkillList skills={event.skills} />
        <ImageGallery directory={event.directory} gallery_size={event.gallery_size} />
      </div>
    </div>
  );
};

export default TimelineEvent;
