import React, { useState, useEffect } from 'react';
import './App.css';
import Project from './components/Project';
import Experience from './components/Experience';
import TimelineEvent from './components/TimelineEvent';

function App() {
  const [bgColor, setBgColor] = useState('rgba(0, 0, 0, 0.5)');
  const [projects, setProjects] = useState([]);
  const [experiences, setExperiences] = useState([]);
  const [timelineEvents, setTimelineEvents] = useState([]);

  // Simulate fetching data from an API
  useEffect(() => {
    setProjects([
      { name: 'Project 1', description: 'Description of Project 1' },
      { name: 'Project 2', description: 'Description of Project 2' },
    ]);

    setExperiences([
      { role: 'Role 1', company: 'Company 1', year: 'Year 1' },
      { role: 'Role 2', company: 'Company 2', year: 'Year 2' },
    ]);

    setTimelineEvents([
      {type:"Internshi AADFEwfewefwe", title:"First Event", date: '2023, May', description: 'Event 1', image: 'image1.png', gallery: ['image1.png', 'image2.png'] },
      {type:"Volunteer", title:"Second Event", date: '2023, Jun', description: 'Event 2', image: 'image2.png' },
    ]);
  }, []);

  useEffect(() => {
    const updateBackground = (e) => {
      const x = e.clientX;
      const y = e.clientY;
      const red = Math.floor((x / window.innerWidth) * 255);
      const green = Math.floor((y / window.innerHeight) * 255);
      setBgColor(`rgba(${red}, ${green}, 200, 0.5)`);
    };

    window.addEventListener('mousemove', updateBackground);

    return () => {
      window.removeEventListener('mousemove', updateBackground);
    };
  }, []);

  return (
    <div className="App" style={{ backgroundColor: bgColor }}>
      <header className="App-header">
        <h1>My Portfolio</h1>
      </header>

      <section className="Projects">
        <h2>Projects</h2>
        {projects.map((project, index) => (
          <Project key={index} project={project} />
        ))}
      </section>

      <section className="Experiences">
        <h2>Experiences</h2>
        {experiences.map((exp, index) => (
          <Experience key={index} experience={exp} />
        ))}
      </section>

      <section className="Timeline">
        <h2>My Journey</h2>
        {timelineEvents.map((event, index) => (
          <TimelineEvent key={index} event={event} />
        ))}
      </section>


    </div>
  );
}

export default App;
