import React, { useEffect } from 'react';

function FallingStars() {

  useEffect(() => {
    const numStars = 100;

    for (let i = 0; i < numStars; i++) {
      let star = document.createElement('img');
      star.className = 'star';
      star.src = `${process.env.PUBLIC_URL}/star-icon.png`;

      let xPos = Math.random() * window.innerWidth;
      let yPos = Math.random() * window.innerHeight;
      let duration = Math.random() * 3 + 2 + 's';

      star.style.left = xPos + 'px';
      star.style.top = yPos + 'px';
      star.style.animationDuration = duration;

      document.querySelector('.star-container').appendChild(star);
    }

  }, []);

  return (
    <div className="star-container">
      {/* Star elements will be appended here */}
    </div>
  );
}

export default FallingStars;
