import React, { useEffect, useState } from 'react';
import './About.css';
import { loadImage } from '../imageLoader';  // Assuming loadImage is correctly exported
import ImageGallery from './ImageGallery';  // Import the ImageGallery component

const About = () => {
    const [mainImageSrc, setMainImageSrc] = useState(null);

    useEffect(() => {
        loadImage("main_image", "./about_images/")
            .then((img) => {
                setMainImageSrc(img.src);
            })
            .catch((error) => {
                console.error('Error loading main image:', error);
            });
    }, []);

    return (
        <section className="about-section">
            <h2>Hala! ðŸ¤™</h2>
            <div className="about-content">
                <div className="about-image">
                    <img src={mainImageSrc} alt="That's Me!" />
                </div>
                <div className="about-text">
                    {/* Your text content here */}
                </div>
            </div>

            <ImageGallery directory="./about_images/" gallery_size={4} />
        </section>
    );
}

export default About;
