export const imageFormats = ['png', 'jpg', 'jpeg', 'gif', 'webp'];

export async function loadImage(imageName, directory) {
  return new Promise((resolve, reject) => {
    const attempts = imageFormats.map((format) => {
      return new Promise((res, rej) => {
        const img = new Image();
        img.src = `${directory}${imageName}.${format}`;

        img.onload = () => res(img);
        img.onerror = () => rej({});
      });
    });

    Promise.any(attempts)
      .then((img) => resolve(img))
      .catch((err) => reject(err));
  });
}


export async function loadGallery(imageNames, directory) {
  const promises = imageNames.map((imageName) => loadImage(imageName, directory));
  return Promise.allSettled(promises);
}
