import React, { useEffect, useState } from 'react';  // Don't forget to import useEffect and useState
import './TimelineEvent.css';  // Import the CSS
import ImageGallery from './ImageGallery';
import { loadImage } from '../imageLoader';  // Assuming loadImage is correctly exported

const TimelineEvent = ({ event }) => {
  const [logoImage, setLogoImage] = useState(null);  // Create state to hold the loaded image URL

  useEffect(() => {
    // load the image asynchronously
    loadImage("logo", event.directory)
      .then((img) => {
        // Once the image is loaded, update the state
        setLogoImage(img.src);
      })
      .catch((error) => {
        console.error('Error loading image:', error);
      });
  }, [event]);  // Dependency array to make sure effect runs when event changes

  return (
    <div className="TimelineEvent-container">
      <div className="TimelineEvent-arrowBox"></div>
      <div className="timeline-event">
        <span className="event-date">{event.date}</span>
        <div className="timeline-top"> 
          <div className="timeline-image">
            <img src={logoImage} alt={event.title} />
          </div>
          <div className="timeline-content">
            <h3>{event.title}</h3>
          </div>
        </div>
        <h2 style={{ marginBottom: '0.5em' }}>{event.type}</h2>  {/* Adjust as needed */}
        <div className="timeline-description" style={{ marginTop: '0.5em' }}>  {/* Adjust as needed */}
          <p dangerouslySetInnerHTML={{ __html: event.description.replace(/\n/g, '<br/>') }} />
        </div>
        <ImageGallery directory={event.directory} gallery_size={event.gallery_size} />
      </div>
    </div>
  );
};

export default TimelineEvent;
