import React, { useState, useEffect } from 'react';
import './App.css';
import Project from './components/Project';
import Experience from './components/Experience';
import TimelineEvent from './components/TimelineEvent';
import FallingStars from './components/FallingStars';  // Import the FallingStars component
import 'font-awesome/css/font-awesome.min.css';

function App() {
  const [bgColor, setBgColor] = useState('rgba(0, 0, 0, 1)');
  const [theme, setTheme] = useState(''); // Add this line for theme change
  const [timelineEvents, setTimelineEvents] = useState([]);
  const [activeTimeline, setActiveTimeline] = useState('projects'); // 'projects', 'volunteer', or 'experiences'

  useEffect(() => {
    // Define your sets of timeline events
    const projectEvents = [
      {
        type: "iOS Developer",
        title: "Social iOS App",
        date: 'Aug 2023 - Present',
        description: `Incorporated Firebase for storage & login authentication.\nIntegrated real-time data syncing between local storage and Cloud Firestore, ensuring a seamless user experience.\nImplemented the MVVM design pattern for a scalable and maintainable codebase.`,
        skills: ['SwiftUI', 'Cloud Firestore', 'Firebase', 'Data Synchronization', 'Model-view-viewmodel (MVVM)', 'Swift (Programming Language)'],
        image: 'image7.png',
        gallery: ['image7.png', 'image8.png']
      },
      {
        type: "Full-stack Developer",
        title: "Algorithm Visualizer",
        date: 'Mar 2022 - May 2022',
        description: `Visualized BST, Min/Max Heap operations.\nBackend: JS & Node.js | Frontend: HTML & CSS.\nBuilt an Undo feature using a stack of deep copied history.\nImplemented a BFS-based node positioning algorithm for binary tree illustration.\nCreated a "current action" box, showing the current action in the algorithm in real-time.`,
        skills: ['JS', 'Node.js', 'HTML', 'CSS'],
        image: 'image9.png',
        gallery: ['image9.png', 'image10.png']
      },
      {
        type: "Data Scientist",
        title: "Data Science Tutorial",
        date: 'Nov 2021 - Dec 2021',
        description: `Created a step-by-step introduction to data science using Jupyter Notebook.\nWalked the reader through Data Collection, Processing, Visualization, and Machine Learning.\nShowcased the steps needed to tidy different types of tables into easy to manipulate data frames.\nDemonstrated skewed data handling techniques, like logarithmic representation and trimming outliers.\nPresented hypothesis testing and prediction with single-variable vs multivariable modules.`,
        skills: ['Python', 'Pandas', 'Jupyter', 'Data Visualization', 'Machine Learning'],
        image: 'image11.png',
        gallery: ['image11.png', 'image12.png']
      },
      {
        type: "Python Developer",
        title: "Maze Generator",
        date: 'Aug 2020',
        description: `Created a visualized random maze generator with the Pygame library.\nImplemented the randomized depth-first search (DFS) method.`,
        skills: ['Python', 'Pygame', 'DFS'],
        image: 'image13.png',
        gallery: ['image13.png', 'image14.png']
      }
    ];



    const volunteerEvents = [
      {
        type: "Mentoring at Bitcamp Hackathon",
        title: "Bitcamp Hackathon",
        date: 'Apr 2023',
        description: "Helped participants with app design and ML implementation at Bitcamp 2023 Hackathon.",
        skills: ["Mentoring", "Machine Learning", "App Design", "Connecting"],
        image: './volunteering_images/bitcamp/logo.png',
        gallery: ['./volunteering_images/bitcamp/image1.jpg', './volunteering_images/bitcamp/image2.jpeg']
      },
      {
        type: "Scorer & Checker at MATHCOUNTS Foundation",
        title: "MATHCOUNTS Competition",
        date: 'Feb 2023',
        description: "Worked in a team of 8, scoring and checking 200+ answer sheets at MATHCOUNTS (the math version of the Spelling Bee).",
        skills: ["Teamwork", "Attention to Detail", "Mathematics", "Communication"],
        image: './volunteering_images/mathcounds/logo.png%v2',
        gallery: ['./volunteering_images/mathcounds/image1.png%v2', './volunteering_images/mathcounts/image2.jpg']
      },
      {
        type: "Mentoring at Technica Hackathon",
        title: "Technica Hackathon",
        date: 'Oct 2022',
        description: "Helped participants with web development and other programming-related issues at Technica Hackathon.",
        skills: ["Web Development", "Problem Solving", "Mentoring", "Active Listening"],
        image: './volunteering_images/technica/logo.png',
        gallery: []
      },
      {
        type: "Church Renovation in Austin",
        title: "The Project, University of Texas Austin",
        date: 'Jan 2020',
        description: "Repainted the walls and restored the doors of a church in Austin.",
        skills: ["Painting", "Restoration", "Manual Labor", "Team Building"],
        image: './volunteering_images/the_project/logo.png',
        gallery: []
      },
      {
        type: "SAT Tutor at my highschool",
        title: "SAT",
        date: 'Sep 2018 - Dec 2018',
        description: "Held bi-weekly sessions for SAT (Math and English) to help 20 younger students in my high school.",
        skills: ["Teaching", "SAT Preparation", "Mathematics", "English", "Leadership"],
        image: './volunteering_images/highschool_tutor/logo.jpeg',
        gallery: []
      }
    ];


    const experienceEvents = [
      {
        type: "Application Developer Intern",
        title: "Human-Data Interaction Group",
        date: 'Sep 2023 - Present',
        description: "Working as part of a development team on Mascot.js, a data visualization API, using JavaScript and React.\nImplementing high-level abstractions and flexible rendering options in SVG and WebGL.",
        skills: ["JavaScript", "React", "SVG", "WebGL", "Teamwork"],
        image: "./experiences_images/hdi_group/logo.png",
        gallery: ['./experiences_images/hdi_group/image1.png']
      },
      {
        type: "Research Intern",
        title: "KAUST AI Initiative",
        date: 'Jun 2023 - Aug 2023',
        description: "Led the design and implementation of a standardized benchmarking pipeline for multi-modal Large Language Models (LLMs), creating an object-oriented structure to encapsulate any model for seamless integration.\nUtilized PyTorch to modify existing LLMs for batched inference, optimizing for A100 GPUs in the IBEX remote cluster.",
        skills: ["PyTorch", "Benchmarking", "GPU Optimization", "Object-Oriented Programming"],
        image: './experiences_images/ai_init/logo.jpg',
        gallery: [
          "./experiences_images/ai_init/image1.JPG",
          "./experiences_images/ai_init/image2.jpg",
          "./experiences_images/ai_init/image3.jpg",
          "./experiences_images/ai_init/image4.jpg",
          "./experiences_images/ai_init/image5.jpg",
          "./experiences_images/ai_init/image6.jpeg"
        ]

      },
      {
        type: "Machine Learning Intern",
        title: "NASA Harvest",
        date: 'Feb 2023 - May 2023',
        description: "Employed Google Earth Engine API to split labeled 1-year image composites into 3-week intervals, overlayed coordinates, and exported imagery to Google Drive.\nUtilized Random Upsampling to balance data, then built a 1D CNN model in TensorFlow and evaluated using F-1 scores.",
        skills: ["Google Earth Engine API", "TensorFlow", "Data Preprocessing", "F-1 Score Evaluation"],
        image: './experiences_images/nasa_harvest/logo.png',
        gallery: [
          './experiences_images/nasa_harvest/image1.jpeg',
          './experiences_images/nasa_harvest/image2.jpeg',
          './experiences_images/nasa_harvest/image3.jpeg',
          './experiences_images/nasa_harvest/image4.jpeg',
          './experiences_images/nasa_harvest/image5.jpeg'
        ]
      },
      {
        type: "Software Engineer Intern",
        title: "Science Systems and Applications, Inc. (SSAI)",
        date: 'Jun 2022 - Aug 2022',
        description: "Collaborated with NASA scientists to develop cloud-detection neural networks using Keras, based on 18 features.\nAchieved 6X faster processing by applying multi-threading and managed a 5-million-observation dataset with Xarray.",
        skills: ["Keras", "Multi-threading", "Data Management", "Visualization"],
        image: "./experiences_images/ssai/logo.png",
        gallery: [
          "./experiences_images/ssai/image1.jpg",
          "./experiences_images/ssai/image2.jpg",
          "./experiences_images/ssai/image3.jpg",
          "./experiences_images/ssai/image4.jpg",
          "./experiences_images/ssai/image5.jpg",
          "./experiences_images/ssai/image6.jpg"
        ]

      }
    ];





    // Set timelineEvents based on activeTimeline
    if (activeTimeline === 'projects') {
      setTimelineEvents(projectEvents);
    } else if (activeTimeline === 'volunteer') {
      setTimelineEvents(volunteerEvents);
    } else if (activeTimeline === 'experiences') {
      setTimelineEvents(experienceEvents);
    }
  }, [activeTimeline]);



  useEffect(() => {
    document.body.style.backgroundColor = bgColor;
    return () => {
      document.body.style.backgroundColor = null;
    };
  }, [bgColor]);


  useEffect(() => {
    const updateBackground = (e) => {
      const x = e.clientX;
      const y = e.clientY;
      const z = (x + y) / 2;

      // Here we are reducing the range to 200-255, which limits the color change
      const red = Math.floor((x / window.innerWidth) * 55 + 200);
      const green = Math.floor((y / window.innerHeight) * 55 + 200);
      const blue = Math.floor((z / ((window.innerWidth + window.innerHeight) / 2)) * 55 + 200);

      setBgColor(`rgba(${red}, ${green}, ${blue}, 0.5)`);  // Alpha set to 0.5 for transparency
    };


    const toggleNightTheme = (e) => {
      if (e.clientX > window.innerWidth / 2) {
        setTheme('App-night');
      } else {
        setTheme('');
      }
    }

    //window.addEventListener('mousemove', updateBackground);
    //window.addEventListener('mousemove', toggleNightTheme);  // Add this line for theme change

    return () => {
      //window.removeEventListener('mousemove', updateBackground);
      //window.removeEventListener('mousemove', toggleNightTheme); // Add this line for theme change
    };
  }, []);

  //const [headerColor, setHeaderColor] = useState("#282c34"); // State to keep track of the header color

  const handleMouseEnter = () => {
    //setHeaderColor("#444"); // Change to a different color when the mouse enters
  };

  const handleMouseLeave = () => {
    //setHeaderColor("#282c34"); // Reset to the original color when the mouse leaves
  };
  //<FallingStars /> {/* Include the FallingStars component */}
  // put this below to make stars

  return (
    <div className={`App ${theme}`} style={{ backgroundColor: bgColor }}>


    <header className="App-header" >
      <h1 className="title-english">Taha Al-Nufaili</h1>

      <div className="TabMenu">
        <button 
          className={activeTimeline === 'projects' ? 'active' : ''} 
          onClick={() => setActiveTimeline('projects')}
        >
          Projects
        </button>
        <button 
          className={activeTimeline === 'volunteer' ? 'active' : ''} 
          onClick={() => setActiveTimeline('volunteer')}
        >
          Volunteer
        </button>
        <button 
          className={activeTimeline === 'experiences' ? 'active' : ''} 
          onClick={() => setActiveTimeline('experiences')}
        >
          Experiences
        </button>
      </div>


      <h1 className="title-arabic">طه النفيلي</h1>

        <div className="social-icons">
          <a href="https://www.youtube.com/@tahaalnufaili8838" target="https://www.youtube.com/@tahaalnufaili8838" rel="noopener noreferrer">
            <i className="fa fa-youtube fa-2x"></i>
          </a>
          <a href="https://www.linkedin.com/in/taha-al-nufaili/" target="https://www.linkedin.com/in/taha-al-nufaili/" rel="noopener noreferrer">
            <i className="fa fa-linkedin fa-2x"></i>
          </a>
          <a href="https://github.com/ta0a2000t" target="https://github.com/ta0a2000t" rel="noopener noreferrer">
            <i className="fa fa-github fa-2x"></i>
          </a>
          <a href="https://www.youtube.com/watch?v=7eAdsUbXkaQ&ab_channel=MrMahir%D9%85%D8%A7%D9%87%D8%B1" target="resume.pdf" rel="noopener noreferrer">  {/* Update this URL to point to your actual resume */}
            <i className="fa fa-file-text fa-2x"></i>
          </a>
        </div>

    </header>




      <section className="Timeline">
        <h2>{activeTimeline === 'projects' ? 'My Projects 🛠' : activeTimeline === 'volunteer' ? 'My Volunteer Work 🤝' : 'My Experiences 🛶'}</h2>
        {timelineEvents.map((event, index) => (
          <TimelineEvent key={index} event={event} />
        ))}
      </section>


    </div>
  );
}

export default App;
