/**********************************************************************
 *
 *  Copyright © 2023 Taha Al-Nufaili
 *  GitHub: https://github.com/ta0a2000t
 *  
 *  Notice: This file is part of a project created by Taha Al-Nufaili. 
 *  Redistribution of this file is permitted. However, modification 
 *  of this file is strictly prohibited without explicit permission 
 *  from the author.
 *
 **********************************************************************/

import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './ImageGallery.css';

// Example: directory = './volunteering_images/mathcounts/'
const ImageGallery = ({ directory, gallery_size }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  // Render empty div if gallery size is 0
  if (gallery_size === 0) {
    return <div style={{ paddingBottom: '1em' }}></div>;
  }

  const gallery = Array.from({ length: gallery_size }, (_, i) => `image${i + 1}.jpg`);

  return (
    <div 
      className={`image-container ${isHovered ? 'scrollable' : ''}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="timeline-gallery">
        {gallery.map((img, index) => (
          <div key={index} className="image-wrapper">
            <img src={`${directory}/${img}`} alt={`Gallery ${img}`} loading="lazy" />
          </div>
        ))}
      </div>
    </div>
  );
};

ImageGallery.propTypes = {
  directory: PropTypes.string.isRequired,
  gallery_size: PropTypes.number.isRequired, 
};

export default ImageGallery;
