import React, { useRef, useEffect } from 'react';
import './TimelineEvent.css';  // Import the CSS

const TimelineEvent = ({ event }) => {
  const galleryImages = event.gallery || [];
  const headerRef = useRef(null);

  useEffect(() => {
    if (headerRef.current) {
      const textWidth = headerRef.current.offsetWidth;
      headerRef.current.style.marginLeft = `${- (textWidth + 30)}px`;
    }
  }, [event]);

  return (
    <div className="TimelineEvent-container">
      <div className="TimelineEvent-arrowBox"></div>
      <div className="timeline-event">


        <div className="timeline-header" ref={headerRef}>
            <span className="event-date">{event.date}</span>
        </div>



        <div className="timeline-image">
          <img src={event.image} alt={event.description} />
        </div>

        <div className="timeline-content">
          <h3>{event.title}</h3>
          <h2>{event.type}</h2>
        </div>

        <div className="timeline-description">
          <p>{event.description}</p>
        </div>




        <div className="timeline-bottom">
            <div className="image-container">
                <div className="timeline-gallery">
                    {galleryImages.map((imgSrc, index) => (
                    <div key={index} className="image-wrapper">
                    <img src={imgSrc} alt={`Gallery Image ${index}`} />
                    </div>
                    ))}
                </div>
            </div>
        </div>

      </div>
    </div>
  );
};

export default TimelineEvent;
