const imageFormats = ['png', 'jpg', 'jpeg', 'gif', 'webp'];

export async function loadImage(imageName, directory) {
  return new Promise((resolve, reject) => {
    let img = new Image();
    let loaded = false;

    imageFormats.forEach((format) => {
      if (loaded) return;

      img.src = `${directory}${imageName}.${format}`;
      console.log(` to load image: ${imageName}.${format}`);

      img.onload = () => {
        loaded = true;
        resolve(img);
      };

      img.onerror = () => {
        console.log(`Failed to load image: ${imageName}.${format}`);
      };
    });

    setTimeout(() => {
      if (!loaded) {
        reject(new Error(`Failed to load ${imageName} in any format.`));
      }
    }, 3000); // 3 seconds timeout
  });
}

export async function loadGallery(imageNames, directory) {
  const images = [];

  for (let imageName of imageNames) {
    try {
      const img = await loadImage(imageName, directory);
      images.push(img);
    } catch (err) {
      console.error(`Failed to load image ${imageName}: ${err}`);
    }
  }

  return images;
}
