import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { loadGallery } from '../imageLoader'; 
import './ImageGallery.css';

                 //example directory = './volunteering_images/mathcounts/'
const ImageGallery = ({ directory, gallery_size }) => {
  const [galleryImages, setGalleryImages] = useState([]);

  useEffect(() => {
    (async () => {
      const imageNames = Array.from({ length: gallery_size }, (_, index) => `image${index + 1}`);

      try {
        const images = await loadGallery(imageNames, directory);
        setGalleryImages(images);
      } catch (error) {
        console.error('An error occurred:', error);
      }
    })();
  }, [directory, gallery_size]);  // Dependency array changed to actually used variables

// Render empty div if gallery_size is 0
if (gallery_size === 0) {
    return <div style={{ paddingBottom: '1em' }}></div>;
    }

  return (
    <div className="image-container">
      <div className="timeline-gallery">
        {galleryImages.map((img, index) => (
          <div key={index} className="image-wrapper">
            <img src={img.src} alt={`Gallery Image ${index}`} />
          </div>
        ))}
      </div>
    </div>
  );
};

ImageGallery.propTypes = {
  directory: PropTypes.string.isRequired,
  gallery_size: PropTypes.number.isRequired,
};

export default ImageGallery;
