import React, { useRef, useEffect } from 'react';
import './TimelineEvent.css';  // Import the CSS
import { loadGallery } from '../imageLoader';

const TimelineEvent = ({ event }) => {
  const [galleryImages, setGalleryImages] = useState([]);

  useEffect(() => {
    (async () => {
      const directory = './volunteering_images/mathcounts/';
      const imageNames = event.gallery; // Assuming the 'gallery' field contains your image names

      try {
        const images = await loadGallery(imageNames, directory);

        // Update the state to re-render component
        setGalleryImages(images);
      } catch (error) {
        console.error('An error occurred:', error);
      }
    })();
  }, [event]); // Dependency array to ensure this runs when 'event' changes



  return (
    <div className="TimelineEvent-container">
      <div className="TimelineEvent-arrowBox"></div>
      <div className="timeline-event">


        <span className="event-date">{event.date}</span>



        <div className="timeline-top"> 
          <div className="timeline-image">
            <img src={event.image} alt={event.title} />
          </div>

          <div className="timeline-content">
            <h3>{event.title}</h3>
          </div>
        </div>

        <h2>{event.type}</h2>

        <div className="timeline-description">
          <p dangerouslySetInnerHTML={{ __html: event.description.replace(/\n/g, '<br/>') }} />
        </div>





        <div className="timeline-bottom">
          <div className="image-container">
            <div className="timeline-gallery">
              {galleryImages.map((img, index) => (
                <div key={index} className="image-wrapper">
                  <img src={img.src} alt={`Gallery Image ${index}`} />
                </div>
              ))}
            </div>
          </div>
        </div>

      </div>
    </div>
  );
};

export default TimelineEvent;
