import React, { useEffect, useState } from 'react';
import './TimelineEvent.css';
import ImageGallery from './ImageGallery';
import { loadImage } from '../imageLoader';

const TimelineEvent = ({ event }) => {
  const [logoImage, setLogoImage] = useState(null);
  const [isLoading, setIsLoading] = useState(true);  // Add loading state

  useEffect(() => {
    setIsLoading(true);  // Set loading to true when starting to load new image
    loadImage("logo", event.directory)
      .then((img) => {
        setLogoImage(img.src);
        setIsLoading(false);  // Set loading to false once image is loaded
      })
      .catch((error) => {
        console.error('Error loading image:', error);
        setIsLoading(false);  // Set loading to false if an error occurs
      });
  }, [event]);

  const EventDescription = ({ description }) => {
    const descriptionList = description.map((line, index) => (
      <li key={index}>{line}</li>
    ));

    return (
      <div>
        <ul className="timeline-description">
          {descriptionList}
        </ul>
      </div>
    );
  };

  return (
    <div className="TimelineEvent-container">
      <div className="TimelineEvent-arrowBox"></div>
      <div className="timeline-event">
        <span className="event-date">{event.date}</span>
        <div className="timeline-top"> 
          <div className="timeline-image">
            {/* Show placeholder or loading indicator while isLoading is true */}
            {isLoading ? (
              <div className="image-placeholder">Loading...</div>
            ) : (
              <img src={logoImage} alt={event.title} />
            )}
          </div>
          <div className="timeline-content">
            <h3>{event.title}</h3>
          </div>
        </div>
        <h2 style={{ marginBottom: '0em' }}>{event.type}</h2>
        <EventDescription description={event.description} style={{ marginTop: '0em' }}/>
        <ImageGallery directory={event.directory} gallery_size={event.gallery_size} />
      </div>
    </div>
  );
};

export default TimelineEvent;
